import React, { ComponentPropsWithoutRef} from 'react';
const styles = require('./styles.css');

type HeaddingProps = ComponentPropsWithoutRef<"div">

type Props = {
  children: string
  className: string
} & HeaddingProps

const Hedding: React.FC<Props> = ({
  children,
  className,
  ...props
}) => {

  level = Math.max(0, Math.min(6, level))
  visualLevel = (typeof visualLevel != "undefined") ? visualLevel : level;
  const Tag = `h${level}`
  const tagStyle = `${styles.h} ${styles[`h${visualLevel}`]}`
  return (
    <div className={[tagStyle, className].join(" ")} {...props}>{ children }</div>
  )
};

export default Hedding;
